package main

import "github.com/dfengwji/pss.common/writing"

func main()  {
testGraph3()
}

func parseHexDots(num uint16, hex string) []*writing.DotInfo {
	c := int(num * writing.DotHexLength)
	if len(hex) != c {
		return nil
	}
	dots := make([]*writing.DotInfo, 0, num)
	var i uint16 = 0
	for i = 0; i < num; i += 1 {
		var dot = new(writing.DotInfo)
		err := dot.ParseHex(hex[i*writing.DotHexLength : (i+1)*writing.DotHexLength])
		if err != nil {
		} else {
			dots = append(dots, dot)
			//fmt.Println(dot.String())
		}
	}
	return dots
}

func testGraph3()  {
	hex := ""
	dots := parseHexDots(uint16(len(hex)/30), hex)
	writing.DrawPoints(dots, "width--6-1")
}